mapper.update:
UPDATE BOARD
SET
TITLE =
#{title},
BOARD_CONTENTS = #{boardContents}
WHERE BOARD_ID = #{boardId}

mapper.select:
SELECT ROW_NUM, BOARD_ID, EMP_NAME, TITLE, BOARD_CONTENTS, TIME, (SELECT COUNT(*) FROM REPLY R WHERE R.BOARD_ID = C.BOARD_ID) RECNT FROM (SELECT ROWNUM ROW_NUM, A.* FROM ( SELECT E.EMP_NAME, B.* FROM BOARD B, EMPLOYEES E WHERE B.EMP_NO = E.EMP_NO AND BOARD_TYPE = #{boardType}
AND TITLE LIKE '%' || #{searchKeyword} || '%'
AND BOARD_CONTENTS LIKE '%' || #{searchKeyword} || '%'
ORDER BY BOARD_ID DESC) A ) C WHERE ROW_NUM BETWEEN #{first} AND #{last}

SELECT COUNT(*) FROM BOARD WHERE BOARD_TYPE = #{boardType}
AND TITLE LIKE '%' || #{searchKeyword} || '%'
AND BOARD_CONTENTS LIKE '%' || #{searchKeyword} || '%'
