mapper.select:
SELECT DEPT_ID, DEPT_NAME, UPPER_DEPT_ID 
FROM DEPARTMENT 
WHERE DEPT_ID = #{deptId}

mapper.update:
UPDATE DEPARTMENT SET
DEPT_ID = #{deptId}
,DEPT_NAME = #{deptName}
,UPPER_DEPT_ID = #{upperDeptId}
WHERE DEPT_ID = #{deptId}

mapper.insert:
INSERT INTO DEPARTMENT(
						DEPT_ID
						,DEPT_NAME

						,UPPER_DEPT_ID)

VALUES ( (SELECT NVL(MAX(DEPT_ID), 0) + 1 FROM DEPARTMENT )
		,#{deptName}

		,#{upperDeptId}

)
