mapper.insert:



			SELECT DOC_ID FROM DOCUMENTS WHERE DOC_NO = #{docType} || #{docTypeSeq}


			SELECT #{docId} DOC_ID FROM DUAL




insert into DOCUMENTDETAIL (
	DOC_ID
	, EMP_NO
	, APPROVAL_ORDER

  	, APPROVAL_DATE

	, STATUS			

  	, APPROVAL_COMMENTS

	)
  values(
  	#{docId}
  	, #{empNo}
  	, #{approvalOrder}

  	, sysdate

  	, #{status}

  	, #{approvalComments}

   )

mapper.update:
SELECT COUNT( NVL( SUBSTR( DOC_NO, INSTR( DOC_NO,'-', 1, 2 ) + 1 ), 0 ) ) + 1 AS DOC_TYPE_SEQ FROM DOCUMENTS WHERE DOC_NO LIKE #{docType}||'%'
UPDATE DOCUMENTS SET DOC_NO = #{docType}||#{docTypeSeq}, DOC_TITLE = #{docTitle}, DOC_CONTENTS = #{docContents}, SECURE_LEVEL = #{secureLevel}
, REG_DATE = NULL , TEMP_YN = 1
, REG_DATE = SYSDATE , RETURN_EMP_NO = NULL , TEMP_YN = 0
DOC_ID=#{docId}

mapper.select:
SELECT A.DOC_ID, A.EMP_NO, B.EMP_NAME, A.APPROVAL_ORDER, A.APPROVAL_DATE, A.STATUS, A.APPROVAL_COMMENTS, CASE WHEN POSITION = '0' THEN '사원' WHEN POSITION = '1' THEN '대리' WHEN POSITION = '2' THEN '과장' WHEN POSITION = '3' THEN '차장' WHEN POSITION = '4' THEN '부장' WHEN POSITION = '5' THEN '대표' ELSE '없음' END AS POSITION , C.DEPT_NAME FROM DOCUMENTDETAIL A JOIN EMPLOYEES B ON A.EMP_NO = B.EMP_NO JOIN DEPARTMENT C ON B.DEPT_ID = C.DEPT_ID
DOC_ID=#{docId}
ORDER BY APPROVAL_ORDER ASC
