mapper.insert:

	select DOC_ID from documents where doc_no = #{docType} || #{docTypeSeq}

insert into DOCUMENTDETAIL (
	DOC_ID
	, EMP_NO
	, APPROVAL_ORDER

  	, APPROVAL_DATE

	, STATUS			

  	, APPROVAL_COMMENTS

	)
  values(
  	#{docId}
  	, #{empNo}
  	, #{approvalOrder}

  	, to_date(#{approvalDate},'yyyy-mm-dd hh24:mi:ss')

  	, #{status}

  	, #{approvalComments}

   )

mapper.select:
SELECT A.DOC_ID, A.EMP_NO, B.EMP_NAME, A.APPROVAL_ORDER, A.APPROVAL_DATE, A.STATUS, A.APPROVAL_COMMENTS, CASE WHEN POSITION = '0' THEN '사원' WHEN POSITION = '1' THEN '대리' WHEN POSITION = '2' THEN '과장' WHEN POSITION = '3' THEN '차장' WHEN POSITION = '4' THEN '부장' WHEN POSITION = '5' THEN '대표' ELSE '없음' END AS POSITION , C.DEPT_NAME FROM DOCUMENTDETAIL A JOIN EMPLOYEES B ON A.EMP_NO = B.EMP_NO JOIN DEPARTMENT C ON B.DEPT_ID = C.DEPT_ID
DOC_ID=#{docId}
ORDER BY APPROVAL_ORDER ASC
