mapper.insert:
select nvl(max(EMAIL_ID),0)+1 from email
INSERT INTO EMAIL( EMAIL_ID, FROM_EMAIL, TO_EMAIL, SUBJECT, EMAIL_CONTENTS, CHECK_TIME, READ_CHECK, EMP_NO ) VALUES( #{emailId} , #{fromEmail}, #{toEmail}, #{subject}, #{emailContents}, sysdate, #{readCheck}, #{empNo}
)

select nvl(max(INBOXID),0)+1 from INBOXEMAIL
INSERT INTO INBOXEMAIL( INBOXID, FROM_INBOX, TO_INBOX, TITLE, INBOX_CONTENTS, CHECK_TIME, READ_CHECK, EMP_NO ) VALUES( #{inboxid} , #{fromEmail}, #{toEmail}, #{subject}, #{inboxContents}, sysdate, #{readCheck}, #{empNo}
)

mapper.select:
select count(decode(read_check,'0','1'))
from inboxemail
where emp_no = #{empNo}
