mapper.insert:
INSERT INTO EMPLOYEES(
						EMP_NO
						,EMP_NAME
						,PASSWORD

						,POSITION

						,JOINDATE

						,EMAIL

						,PHONE

						,ADDRESS

						,DEPT_ID

						,DELETED_YN

						,DELETED_DATE

						,GMAIL_APP_KEY

						)
VALUES ( (SELECT NVL(MAX(EMP_NO), 0) + 1 FROM EMPLOYEES )
		,#{empName}
		,#{password}

		,#{position}

		,#{joindate1}

		,#{email}

		,#{phone}

		,#{address}

		,#{deptId}

		,#{deletedYn}

		,#{deletedDate}

		,#{gmailAppKey}

		)

mapper.update:
UPDATE EMPLOYEES SET
EMP_NAME = #{empName}
,PASSWORD = #{password}
,POSITION = #{position}
,EMAIL = #{email}
,PHONE = #{phone}
,ADDRESS = #{address}
,DEPT_ID = #{deptId}
,GMAIL_APP_KEY = #{gmailAppKey}
WHERE EMP_NO = #{empNo}
