mapper.insert:
INSERT INTO ROOM ( ROOM_ID, ROOM_NAME, ROOM_LOC, ROOM_SIZE, ROOM_MAX
) VALUES ( #{roomId}, #{roomName}, #{roomLoc}, #{roomSize}, #{roomMax}
)

mapper.update:
UPDATE room
SET ROOM_NAME = #{roomName},
ROOM_LOC = #{roomLoc},
ROOM_SIZE = #{roomSize},
ROOM_MAX = #{roomMax}
WHERE ROOM_ID = #{roomId}
